12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455#include&lt;iostream&gt;using namespace std;long long int b[40][40];int main()&#123;    int n,m;    cin&gt;&gt;n&gt;&gt;m;    long long int a[40][40];    long long int t[40][40];    for(int i=0;i&lt;n;i++)&#123;        for(int j=0;j&lt;n;j++)&#123;            cin&gt;&gt;a[i][j];            t[i][j]=a[i][j];        &#125;    &#125;    //0é˜¶å¹‚    if(m==0)&#123;        for(int i=0;i&lt;n;i++)&#123;            for(int j=0;j&lt;n;j++)&#123;                if(i!=j)&#123;                    cout&lt;&lt;0&lt;&lt;" ";                &#125;else&#123;                    cout&lt;&lt;1&lt;&lt;" ";                &#125;            &#125;            cout&lt;&lt;endl;        &#125;        return 0;    &#125;    //ä¸€èˆ¬æƒ…å†µ    while(--m)&#123;        for(int i=0;i&lt;n;i++)&#123;            for(int j=0;j&lt;n;j++)&#123;                int k=n;                while(k)&#123;                    b[i][j]+=t[i][k-1]*a[k-1][j];                    k--;                &#125;            &#125;        &#125;        for(int i=0;i&lt;n;i++)&#123;            for(int j=0;j&lt;n;j++)&#123;                t[i][j]=b[i][j];                b[i][j]=0;            &#125;        &#125;    &#125;    //è¾“å‡ºç»“æœ    for(int i=0;i&lt;n;i++)&#123;             for(int j=0;j&lt;n;j++)&#123;            cout&lt;&lt;t[i][j]&lt;&lt;" ";        &#125;        cout&lt;&lt;endl;    &#125;    return 0;&#125;

